import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { motion } from 'framer-motion';
import { ExternalLink, Heart, Users, Award, Star, Building, Search, Filter } from 'lucide-react';
import { api } from '../utils/api';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Sponsors = () => {
  const { data, isLoading } = useQuery(
    'sponsors',
    () => api.get('/sponsors').then(res => res.data)
  );

  const groupedSponsors = SPONSOR_TIERS.reduce((acc, tier) => {
    acc[tier.id] = data?.sponsors?.filter(sponsor => sponsor.tier === tier.id) || [];
    return acc;
  }, {});

  const benefits = [
    {
      icon: Heart,
      title: 'Brand Visibility',
      description: 'Get your brand in front of talented tech students and future innovators.'
    },
    {
      icon: Users,
      title: 'Talent Pipeline',
      description: 'Connect with skilled students for internships and full-time opportunities.'
    },
    {
      icon: Award,
      title: 'Innovation Partnership',
      description: 'Collaborate on cutting-edge projects and research initiatives.'
    }
  ];

  return (
    <div className="page-container bg-gray-950">
      {/* Hero Section */}
      <section className="bg-gradient-to-br from-primary-50 via-white to-purple-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-6">
              Our <span className="text-gradient">Sponsors</span>
            </h1>
            <p className="text-xl text-secondary-600 max-w-3xl mx-auto">
              We're grateful to our amazing sponsors who support our mission to foster innovation and excellence in technology.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Current Sponsors */}
      {!isLoading && data?.sponsors && data.sponsors.length > 0 && (
        <section className="bg-white section-padding">
          <div className="container-max">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6 }}
              viewport={{ once: true }}
              className="text-center mb-12"
            >
              <h2 className="text-3xl font-bold text-secondary-900 mb-4">Current Sponsors</h2>
              <p className="text-secondary-600">Thank you to these organizations for their continued support</p>
            </motion.div>

            <div className="space-y-12">
              {SPONSOR_TIERS.map((tier) => {
                const tierSponsors = groupedSponsors[tier.id];
                if (!tierSponsors || tierSponsors.length === 0) return null;

                return (
                  <motion.div
                    key={tier.id}
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6 }}
                    viewport={{ once: true }}
                  >
                    <div className="text-center mb-8">
                      <span className={`inline-block px-4 py-2 rounded-full text-sm font-medium ${tier.color} mb-2`}>
                        {tier.name} Sponsors
                      </span>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                      {tierSponsors.map((sponsor, index) => (
                        <motion.div
                          key={sponsor._id}
                          initial={{ opacity: 0, y: 20 }}
                          whileInView={{ opacity: 1, y: 0 }}
                          transition={{ duration: 0.6, delay: index * 0.1 }}
                          viewport={{ once: true }}
                          className="card text-center hover:shadow-lg transition-all duration-200"
                        >
                          {sponsor.logo?.url && (
                            <div className="h-24 flex items-center justify-center mb-4">
                              <img
                                src={sponsor.logo.url}
                                alt={sponsor.name}
                                className="max-h-full max-w-full object-contain"
                              />
                            </div>
                          )}

                          <h3 className="text-xl font-semibold text-secondary-900 mb-2">{sponsor.name}</h3>

                          {sponsor.description && (
                            <p className="text-secondary-600 mb-4">{sponsor.description}</p>
                          )}

                          {sponsor.website && (
                            <a
                              href={sponsor.website}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="inline-flex items-center text-primary-600 hover:text-primary-700 font-medium"
                            >
                              Visit Website
                              <ExternalLink size={16} className="ml-1" />
                            </a>
                          )}
                        </motion.div>
                      ))}
                    </div>
                  </motion.div>
                );
              })}
            </div>
          </div>
        </section>
      )}

      {/* Why Sponsor Us */}
      <section className="bg-secondary-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-center mb-12"
          >
            <h2 className="text-3xl font-bold text-secondary-900 mb-4">Why Sponsor MIBCS?</h2>
            <p className="text-secondary-600 max-w-2xl mx-auto">
              Partner with us to support the next generation of tech innovators while gaining valuable benefits for your organization.
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
            {benefits.map((benefit, index) => {
              const Icon = benefit.icon;
              return (
                <motion.div
                  key={benefit.title}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                  className="card text-center"
                >
                  <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 rounded-full mb-4">
                    <Icon size={24} className="text-primary-600" />
                  </div>
                  <h3 className="text-xl font-semibold text-secondary-900 mb-3">{benefit.title}</h3>
                  <p className="text-secondary-600">{benefit.description}</p>
                </motion.div>
              );
            })}
          </div>

          {/* CTA */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-center"
          >
            <div className="bg-white rounded-xl shadow-sm p-8 max-w-2xl mx-auto">
              <h3 className="text-2xl font-bold text-secondary-900 mb-4">Ready to Partner with Us?</h3>
              <p className="text-secondary-600 mb-6">
                Join our community of sponsors and help us continue fostering innovation in technology education.
              </p>
              <div className="flex flex-col sm:flex-row gap-4 justify-center">
                <a href="/contact" className="btn-primary">
                  Become a Sponsor
                </a>
                <a href="mailto:sponsors@mibcs.com" className="btn-outline">
                  Contact Us
                </a>
              </div>
            </div>
          </motion.div>
        </div>
      </section>

      {isLoading && (
        <div className="flex justify-center py-12">
          <LoadingSpinner size="lg" />
        </div>
      )}
    </div>
  );
};

export default Sponsors;
