import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { motion } from 'framer-motion';
import { Search, Filter } from 'lucide-react';
import { api } from '../utils/api';
import { DOMAINS, PROJECT_STATUS } from '../utils/constants';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Projects = () => {
  const [filters, setFilters] = useState({
    status: '',
    domain: '',
    search: ''
  });

  const { data, isLoading } = useQuery(
    ['projects', filters],
    () => {
      const params = new URLSearchParams();
      if (filters.status) params.append('status', filters.status);
      if (filters.domain) params.append('domain', filters.domain);
      return api.get(`/projects?${params.toString()}`).then(res => res.data);
    }
  );

  const filteredProjects = data?.projects?.filter(project =>
    project.title.toLowerCase().includes(filters.search.toLowerCase()) ||
    project.description.toLowerCase().includes(filters.search.toLowerCase())
  ) || [];

  return (
    <div className="min-h-screen">
      <section className="bg-gradient-to-br from-primary-50 via-white to-purple-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-6">
              Our <span className="text-gradient">Projects</span>
            </h1>
            <p className="text-xl text-secondary-600 max-w-3xl mx-auto">
              Discover the innovative projects our members are working on across various technology domains.
            </p>
          </motion.div>

          {/* Filters */}
          <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="relative">
                <Search size={18} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400" />
                <input
                  type="text"
                  placeholder="Search projects..."
                  className="input-field pl-10"
                  value={filters.search}
                  onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                />
              </div>
              <select
                className="input-field"
                value={filters.status}
                onChange={(e) => setFilters(prev => ({ ...prev, status: e.target.value }))}
              >
                <option value="">All Status</option>
                {PROJECT_STATUS.map(status => (
                  <option key={status.id} value={status.id}>{status.name}</option>
                ))}
              </select>
              <select
                className="input-field"
                value={filters.domain}
                onChange={(e) => setFilters(prev => ({ ...prev, domain: e.target.value }))}
              >
                <option value="">All Domains</option>
                {DOMAINS.map(domain => (
                  <option key={domain.id} value={domain.id}>{domain.name}</option>
                ))}
              </select>
              <button className="btn-outline">
                <Filter size={18} className="mr-2" />
                More Filters
              </button>
            </div>
          </div>

          {/* Projects Grid */}
          {isLoading ? (
            <div className="flex justify-center py-12">
              <LoadingSpinner size="lg" />
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredProjects.map((project, index) => (
                <motion.div
                  key={project._id}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                  className="card hover:shadow-lg transition-all duration-200"
                >
                  <div className="space-y-4">
                    <div className="flex items-start justify-between">
                      <h3 className="text-xl font-semibold text-secondary-900">{project.title}</h3>
                      <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                        PROJECT_STATUS.find(s => s.id === project.status)?.color || 'bg-gray-100 text-gray-800'
                      }`}>
                        {PROJECT_STATUS.find(s => s.id === project.status)?.name || project.status}
                      </span>
                    </div>

                    <p className="text-secondary-600 line-clamp-3">
                      {project.shortDescription || project.description}
                    </p>

                    {project.techStack && project.techStack.length > 0 && (
                      <div className="flex flex-wrap gap-2">
                        {project.techStack.slice(0, 3).map((tech, techIndex) => (
                          <span
                            key={techIndex}
                            className="px-2 py-1 bg-secondary-100 text-secondary-700 text-xs rounded"
                          >
                            {tech}
                          </span>
                        ))}
                        {project.techStack.length > 3 && (
                          <span className="px-2 py-1 bg-secondary-100 text-secondary-700 text-xs rounded">
                            +{project.techStack.length - 3} more
                          </span>
                        )}
                      </div>
                    )}

                    {project.domains && project.domains.length > 0 && (
                      <div className="flex flex-wrap gap-2">
                        {project.domains.map((domain) => (
                          <span
                            key={domain}
                            className="px-2 py-1 bg-primary-100 text-primary-800 text-xs rounded-md"
                          >
                            {domain}
                          </span>
                        ))}
                      </div>
                    )}
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {filteredProjects.length === 0 && !isLoading && (
            <div className="text-center py-12">
              <p className="text-secondary-600">No projects found matching your criteria.</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Projects;
