import React, { useState } from 'react';
import { useForm } from 'react-hook-form';
import { motion } from 'framer-motion';
import { Mail, Phone, MapPin, Send, Github, Linkedin, Twitter, Instagram } from 'lucide-react';
import { toast } from 'react-hot-toast';
import { api } from '../utils/api';
import { CLUB_INFO } from '../utils/constants';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Contact = () => {
  const [isSubmitting, setIsSubmitting] = useState(false);

  const {
    register,
    handleSubmit,
    reset,
    formState: { errors }
  } = useForm();

  const onSubmit = async (data) => {
    setIsSubmitting(true);
    try {
      await api.post('/contact', data);
      toast.success('Message sent successfully! We\'ll get back to you soon.');
      reset();
    } catch (error) {
      toast.error('Failed to send message. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const contactInfo = [
    {
      icon: Mail,
      title: 'Email',
      value: CLUB_INFO.email,
      link: `mailto:${CLUB_INFO.email}`
    },
    {
      icon: Phone,
      title: 'Phone',
      value: CLUB_INFO.phone,
      link: `tel:${CLUB_INFO.phone}`
    },
    {
      icon: MapPin,
      title: 'Address',
      value: CLUB_INFO.address,
      link: null
    }
  ];

  const socialLinks = [
    { name: 'GitHub', href: CLUB_INFO.social.github, icon: Github },
    { name: 'LinkedIn', href: CLUB_INFO.social.linkedin, icon: Linkedin },
    { name: 'Twitter', href: CLUB_INFO.social.twitter, icon: Twitter },
    { name: 'Instagram', href: CLUB_INFO.social.instagram, icon: Instagram }
  ];

  return (
    <div className="min-h-screen">
