import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { motion } from 'framer-motion';
import { Search, Filter, Calendar } from 'lucide-react';
import { api } from '../utils/api';
import { DOMAINS, EVENT_TYPES } from '../utils/constants';
import LoadingSpinner from '../components/UI/LoadingSpinner';
import EventCard from '../components/Events/EventCard';

const Events = () => {
  const [activeTab, setActiveTab] = useState('upcoming');
  const [filters, setFilters] = useState({
    type: '',
    domain: '',
    search: ''
  });

  const { data, isLoading } = useQuery(
    ['events', activeTab, filters],
    () => {
      const params = new URLSearchParams();
      params.append('status', activeTab);
      if (filters.type) params.append('type', filters.type);
      if (filters.domain) params.append('domain', filters.domain);
      return api.get(`/events?${params.toString()}`).then(res => res.data);
    }
  );

  const filteredEvents = data?.events?.filter(event =>
    event.title.toLowerCase().includes(filters.search.toLowerCase()) ||
    event.description.toLowerCase().includes(filters.search.toLowerCase())
  ) || [];

  const tabs = [
    { id: 'upcoming', name: 'Upcoming Events', icon: Calendar },
    { id: 'completed', name: 'Past Events', icon: Calendar }
  ];

  return (
    <div className="min-h-screen">
      <section className="bg-gradient-to-br from-primary-50 via-white to-purple-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-6">
              Our <span className="text-gradient">Events</span>
            </h1>
            <p className="text-xl text-secondary-600 max-w-3xl mx-auto">
              Join us for exciting workshops, seminars, and networking events designed to enhance your technical skills.
            </p>
          </motion.div>

          {/* Tabs */}
          <div className="flex justify-center mb-8">
            <div className="bg-white rounded-lg p-1 shadow-sm">
              {tabs.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 px-6 py-3 rounded-md font-medium transition-colors duration-200 ${
                      activeTab === tab.id
                        ? 'bg-primary-600 text-white'
                        : 'text-secondary-600 hover:text-secondary-900'
                    }`}
                  >
                    <Icon size={18} />
                    <span>{tab.name}</span>
                  </button>
                );
              })}
            </div>
          </div>

          {/* Filters */}
          <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="relative">
                <Search size={18} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400" />
                <input
                  type="text"
                  placeholder="Search events..."
                  className="input-field pl-10"
                  value={filters.search}
                  onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                />
              </div>
              <select
                className="input-field"
                value={filters.type}
                onChange={(e) => setFilters(prev => ({ ...prev, type: e.target.value }))}
              >
                <option value="">All Types</option>
                {EVENT_TYPES.map(type => (
                  <option key={type.id} value={type.id}>{type.name}</option>
                ))}
              </select>
              <select
                className="input-field"
                value={filters.domain}
                onChange={(e) => setFilters(prev => ({ ...prev, domain: e.target.value }))}
              >
                <option value="">All Domains</option>
                {DOMAINS.map(domain => (
                  <option key={domain.id} value={domain.id}>{domain.name}</option>
                ))}
              </select>
              <button className="btn-outline">
                <Filter size={18} className="mr-2" />
                More Filters
              </button>
            </div>
          </div>

          {/* Events Grid */}
          {isLoading ? (
            <div className="flex justify-center py-12">
              <LoadingSpinner size="lg" />
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredEvents.map((event, index) => (
                <motion.div
                  key={event._id}
                  initial={{ opacity: 0, y: 20 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                >
                  <EventCard event={event} />
                </motion.div>
              ))}
            </div>
          )}

          {filteredEvents.length === 0 && !isLoading && (
            <div className="text-center py-12">
              <p className="text-secondary-600">No events found matching your criteria.</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Events;
