import React from 'react';
import { motion } from 'framer-motion';
import { DOMAINS } from '../utils/constants';

const Domains = () => {
  return (
    <div className="min-h-screen">
      <section className="bg-gradient-to-br from-primary-50 via-white to-purple-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-6">
              Our <span className="text-gradient">Domains</span>
            </h1>
            <p className="text-xl text-secondary-600 max-w-3xl mx-auto">
              Explore the cutting-edge technology domains that drive our innovation and learning initiatives.
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {DOMAINS.map((domain, index) => (
              <motion.div
                key={domain.id}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="card hover:shadow-xl transition-all duration-300"
              >
                <div className="flex items-center space-x-4 mb-6">
                  <div className="text-4xl">{domain.icon}</div>
                  <div>
                    <h2 className="text-2xl font-bold text-secondary-900">{domain.name}</h2>
                    <p className="text-secondary-600">{domain.description}</p>
                  </div>
                </div>

                <div className="space-y-4">
                  <h3 className="text-lg font-semibold text-secondary-900">Focus Areas:</h3>
                  <div className="grid grid-cols-1 gap-2">
                    {domain.focusAreas.map((area, areaIndex) => (
                      <div key={areaIndex} className="flex items-center space-x-3">
                        <div className="w-2 h-2 bg-primary-600 rounded-full"></div>
                        <span className="text-secondary-700">{area}</span>
                      </div>
                    ))}
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Domains;
