import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { motion, AnimatePresence } from 'framer-motion';
import { Search, Filter, Calendar, Clock, MapPin, Users, ExternalLink, Play, Zap, ChevronDown, X } from 'lucide-react';
import { api } from '../utils/api';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Events = () => {
  const [activeTab, setActiveTab] = useState('upcoming');
  const [filters, setFilters] = useState({
    type: '',
    domain: '',
    search: '',
    location: '',
    speaker: ''
  });
  const [showMoreFilters, setShowMoreFilters] = useState(false);

  const { data, isLoading } = useQuery(
    ['events', activeTab, filters],
    () => {
      const params = new URLSearchParams();
      params.append('status', activeTab);
      if (filters.type) params.append('type', filters.type);
      if (filters.domain) params.append('domain', filters.domain);
      return api.get(`/events?${params.toString()}`).then(res => res.data);
    }
  );

  // Mock events for demo
  const mockEvents = [
    {
      _id: '1',
      title: 'AI Workshop: Building Neural Networks',
      description: 'Learn to build and train neural networks from scratch using TensorFlow and PyTorch.',
      date: '2024-02-15',
      time: '14:00',
      location: 'Tech Lab A',
      type: 'workshop',
      domain: 'Machine Learning',
      capacity: 50,
      registered: 35,
      status: 'upcoming',
      speaker: 'Dr. Sarah Chen',
      registrationUrl: 'https://forms.google.com/ai-workshop'
    },
    {
      _id: '2',
      title: 'Blockchain Hackathon 2024',
      description: '48-hour hackathon focused on building decentralized applications and smart contracts.',
      date: '2024-02-20',
      time: '09:00',
      location: 'Innovation Hub',
      type: 'hackathon',
      domain: 'Blockchain',
      capacity: 100,
      registered: 87,
      status: 'upcoming',
      speaker: 'Multiple Mentors',
      registrationUrl: 'https://forms.google.com/blockchain-hack'
    },
    {
      _id: '3',
      title: 'IoT Security Best Practices',
      description: 'Comprehensive seminar on securing IoT devices and networks against cyber threats.',
      date: '2024-02-25',
      time: '16:00',
      location: 'Auditorium B',
      type: 'seminar',
      domain: 'Cyber Security',
      capacity: 80,
      registered: 62,
      status: 'upcoming',
      speaker: 'Prof. Michael Rodriguez',
      registrationUrl: 'https://forms.google.com/iot-security'
    }
  ];

  const events = data?.events || mockEvents;
  const filteredEvents = events.filter(event => {
    const matchesSearch = event.title.toLowerCase().includes(filters.search.toLowerCase()) ||
                         event.description.toLowerCase().includes(filters.search.toLowerCase());
    const matchesLocation = !filters.location || event.location.includes(filters.location);
    const matchesSpeaker = !filters.speaker || event.speaker.toLowerCase().includes(filters.speaker.toLowerCase());

    return matchesSearch && matchesLocation && matchesSpeaker;
  });

  const tabs = [
    { id: 'upcoming', name: 'Upcoming', icon: Calendar, count: filteredEvents.filter(e => e.status === 'upcoming').length },
    { id: 'completed', name: 'Past Events', icon: Clock, count: filteredEvents.filter(e => e.status === 'completed').length }
  ];

  const getEventTypeColor = (type) => {
    const colors = {
      'workshop': 'bg-blue-500/20 text-blue-400 border-blue-500/30',
      'seminar': 'bg-green-500/20 text-green-400 border-green-500/30',
      'hackathon': 'bg-purple-500/20 text-purple-400 border-purple-500/30',
      'networking': 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30'
    };
    return colors[type] || 'bg-gray-500/20 text-gray-400 border-gray-500/30';
  };

  return (
    <div className="page-container bg-gray-950">
      {/* Hero Section */}
      <section className="relative section-padding overflow-hidden">
        <div className="absolute inset-0">
          <div className="absolute inset-0 bg-gradient-to-br from-gray-950 via-gray-900 to-gray-950"></div>
          <div className="matrix-bg opacity-10"></div>
        </div>

        <div className="container-max relative z-10">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            className="text-center mb-12"
          >
            <h1 className="text-5xl md:text-6xl font-bold text-white mb-6">
              Tech <span className="text-gradient">Events</span>
            </h1>
            <p className="text-xl text-gray-400 max-w-3xl mx-auto leading-relaxed">
              Join us for exciting workshops, hackathons, and seminars designed to enhance your technical skills 
              and connect with fellow innovators.
            </p>
          </motion.div>

          {/* Tabs */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 0.6 }}
            className="flex justify-center mb-8"
          >
            <div className="bg-gray-800/50 backdrop-blur-sm rounded-xl p-1 border border-gray-700">
              {tabs.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 px-6 py-3 rounded-lg font-medium transition-all duration-300 ${
                      activeTab === tab.id
                        ? 'bg-gradient-to-r from-cyan-500 to-blue-600 text-white shadow-lg'
                        : 'text-gray-400 hover:text-cyan-400 hover:bg-gray-700/50'
                    }`}
                  >
                    <Icon size={18} />
                    <span>{tab.name}</span>
                    {tab.count > 0 && (
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        activeTab === tab.id ? 'bg-white/20' : 'bg-gray-600'
                      }`}>
                        {tab.count}
                      </span>
                    )}
                  </button>
                );
              })}
            </div>
          </motion.div>

          {/* Filters */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.5, duration: 0.6 }}
            className="card-glow mb-12"
          >
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
              <div className="relative">
                <Search size={18} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search events..."
                  className="input-field pl-10"
                  value={filters.search}
                  onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                />
              </div>
              <select
                className="select-field"
                value={filters.type}
                onChange={(e) => setFilters(prev => ({ ...prev, type: e.target.value }))}
              >
                <option value="">All Types</option>
                <option value="workshop">Workshop</option>
                <option value="seminar">Seminar</option>
                <option value="hackathon">Hackathon</option>
                <option value="networking">Networking</option>
              </select>
              <select
                className="select-field"
                value={filters.domain}
                onChange={(e) => setFilters(prev => ({ ...prev, domain: e.target.value }))}
              >
                <option value="">All Domains</option>
                <option value="Machine Learning">Machine Learning</option>
                <option value="Internet of Things">Internet of Things</option>
                <option value="Blockchain">Blockchain</option>
                <option value="Cyber Security">Cyber Security</option>
              </select>
              <button 
                className="btn-outline flex items-center space-x-2"
                onClick={() => setShowMoreFilters(!showMoreFilters)}
              >
                <Filter size={18} />
                <span>More Filters</span>
                <motion.div
                  animate={{ rotate: showMoreFilters ? 180 : 0 }}
                  transition={{ duration: 0.2 }}
                >
                  <ChevronDown size={16} />
                </motion.div>
              </button>
            </div>

            {/* More Filters Dropdown */}
            <AnimatePresence>
              {showMoreFilters && (
                <motion.div
                  initial={{ opacity: 0, height: 0 }}
                  animate={{ opacity: 1, height: 'auto' }}
                  exit={{ opacity: 0, height: 0 }}
                  transition={{ duration: 0.3 }}
                  className="mt-4 pt-4 border-t border-gray-700"
                >
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Location</label>
                      <select
                        className="select-field"
                        value={filters.location}
                        onChange={(e) => setFilters(prev => ({ ...prev, location: e.target.value }))}
                      >
                        <option value="">All Locations</option>
                        <option value="Tech Lab A">Tech Lab A</option>
                        <option value="Innovation Hub">Innovation Hub</option>
                        <option value="Auditorium B">Auditorium B</option>
                        <option value="Online">Online</option>
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-300 mb-2">Speaker</label>
                      <input
                        type="text"
                        placeholder="Search by speaker..."
                        className="input-field"
                        value={filters.speaker}
                        onChange={(e) => setFilters(prev => ({ ...prev, speaker: e.target.value }))}
                      />
                    </div>
                    <div className="flex items-end">
                      <button
                        onClick={() => setFilters({
                          type: '',
                          domain: '',
                          search: '',
                          location: '',
                          speaker: ''
                        })}
                        className="btn-secondary flex items-center space-x-2 w-full"
                      >
                        <X size={16} />
                        <span>Clear All</span>
                      </button>
                    </div>
                  </div>
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        </div>
      </section>

      {/* Events Grid */}
      <section className="section-padding">
        <div className="container-max">
          {isLoading ? (
            <div className="flex justify-center py-12">
              <LoadingSpinner size="lg" />
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {filteredEvents.filter(event => event.status === activeTab).map((event, index) => (
                <motion.div
                  key={event._id}
                  initial={{ opacity: 0, y: 30 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.6, delay: index * 0.1 }}
                  viewport={{ once: true }}
                  className="card-glow group hover:scale-105 transition-all duration-300"
                >
                  <div className="space-y-4">
                    {/* Header */}
                    <div className="flex items-start justify-between">
                      <span className={`px-3 py-1 rounded-full text-xs font-medium border ${getEventTypeColor(event.type)}`}>
                        {event.type}
                      </span>
                      <span className="text-xs text-gray-500 code-font">
                        {new Date(event.date).toLocaleDateString()}
                      </span>
                    </div>

                    {/* Title & Description */}
                    <div>
                      <h3 className="text-xl font-semibold text-white group-hover:text-cyan-400 transition-colors duration-300 mb-2">
                        {event.title}
                      </h3>
                      <p className="text-gray-400 text-sm leading-relaxed">
                        {event.description}
                      </p>
                    </div>

                    {/* Event Details */}
                    <div className="space-y-2">
                      <div className="flex items-center space-x-2 text-sm text-gray-500">
                        <Clock size={14} />
                        <span>{event.time}</span>
                      </div>
                      <div className="flex items-center space-x-2 text-sm text-gray-500">
                        <MapPin size={14} />
                        <span>{event.location}</span>
                      </div>
                      <div className="flex items-center space-x-2 text-sm text-gray-500">
                        <Users size={14} />
                        <span>{event.registered}/{event.capacity} registered</span>
                      </div>
                    </div>

                    {/* Domain Badge */}
                    <div className="flex items-center space-x-2">
                      <span className="px-3 py-1 bg-cyan-500/20 text-cyan-400 text-xs rounded-full border border-cyan-500/30">
                        {event.domain}
                      </span>
                    </div>

                    {/* Speaker */}
                    <div className="text-sm text-gray-400">
                      <span className="font-medium">Speaker:</span> {event.speaker}
                    </div>

                    {/* Registration Progress */}
                    <div className="space-y-2">
                      <div className="flex justify-between text-sm">
                        <span className="text-gray-400">Registration</span>
                        <span className="text-cyan-400">{Math.round((event.registered / event.capacity) * 100)}%</span>
                      </div>
                      <div className="w-full bg-gray-800 rounded-full h-2">
                        <div 
                          className="bg-gradient-to-r from-cyan-500 to-blue-600 h-2 rounded-full transition-all duration-300"
                          style={{ width: `${(event.registered / event.capacity) * 100}%` }}
                        ></div>
                      </div>
                    </div>

                    {/* Action Button */}
                    <div className="pt-4 border-t border-gray-800">
                      {event.status === 'upcoming' ? (
                        <a
                          href={event.registrationUrl}
                          target="_blank"
                          rel="noopener noreferrer"
                          className="w-full btn-primary flex items-center justify-center space-x-2"
                        >
                          <ExternalLink size={16} />
                          <span>Register Now</span>
                        </a>
                      ) : (
                        <button className="w-full btn-secondary flex items-center justify-center space-x-2">
                          <Play size={16} />
                          <span>View Recording</span>
                        </button>
                      )}
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
          )}

          {filteredEvents.filter(event => event.status === activeTab).length === 0 && !isLoading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="text-center py-12"
            >
              <div className="w-24 h-24 bg-gray-800 rounded-full flex items-center justify-center mx-auto mb-4">
                <Calendar size={32} className="text-gray-600" />
              </div>
              <p className="text-gray-400 text-lg">No {activeTab} events found.</p>
              <p className="text-gray-500 text-sm mt-2">Check back soon for new events!</p>
            </motion.div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="section-padding bg-gray-900/50">
        <div className="container-max text-center">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
