import React, { useState } from 'react';
import { useQuery } from 'react-query';
import { motion } from 'framer-motion';
import { Search, Github, Linkedin, Globe, Users, Code, Award, Mail } from 'lucide-react';
import { api } from '../utils/api';
import LoadingSpinner from '../components/UI/LoadingSpinner';

const Members = () => {
  const [filters, setFilters] = useState({
    team: '',
    domain: '',
    search: ''
  });

  const { data, isLoading } = useQuery(
    ['members', filters],
    () => {
      const params = new URLSearchParams();
      if (filters.team) params.append('team', filters.team);
      if (filters.domain) params.append('domain', filters.domain);
      return api.get(`/members?${params.toString()}`).then(res => res.data);
    }
  );

  const filteredMembers = data?.members?.filter(member =>
    member.name.toLowerCase().includes(filters.search.toLowerCase()) ||
    member.role.toLowerCase().includes(filters.search.toLowerCase())
  ) || [];

  const groupedMembers = TEAM_TYPES.reduce((acc, teamType) => {
    acc[teamType.id] = filteredMembers.filter(member => member.team === teamType.id);
    return acc;
  }, {});

  return (
    <div className="page-container bg-gray-950">
      <section className="bg-gradient-to-br from-primary-50 via-white to-purple-50 section-padding">
        <div className="container-max">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center mb-12"
          >
            <h1 className="text-4xl md:text-5xl font-bold text-secondary-900 mb-6">
              Our <span className="text-gradient">Team</span>
            </h1>
            <p className="text-xl text-secondary-600 max-w-3xl mx-auto">
              Meet the passionate individuals who drive innovation and excellence at MIBCS.
            </p>
          </motion.div>

          {/* Filters */}
          <div className="bg-white rounded-xl shadow-sm p-6 mb-8">
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <div className="relative">
                <Search size={18} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-secondary-400" />
                <input
                  type="text"
                  placeholder="Search members..."
                  className="input-field pl-10"
                  value={filters.search}
                  onChange={(e) => setFilters(prev => ({ ...prev, search: e.target.value }))}
                />
              </div>
              <select
                className="input-field"
                value={filters.team}
                onChange={(e) => setFilters(prev => ({ ...prev, team: e.target.value }))}
              >
                <option value="">All Teams</option>
                {TEAM_TYPES.map(team => (
                  <option key={team.id} value={team.id}>{team.name}</option>
                ))}
              </select>
              <select
                className="input-field"
                value={filters.domain}
                onChange={(e) => setFilters(prev => ({ ...prev, domain: e.target.value }))}
              >
                <option value="">All Domains</option>
                {DOMAINS.map(domain => (
                  <option key={domain.id} value={domain.id}>{domain.name}</option>
                ))}
              </select>
            </div>
          </div>

          {isLoading ? (
            <div className="flex justify-center py-12">
              <LoadingSpinner size="lg" />
            </div>
          ) : (
            <div className="space-y-12">
              {TEAM_TYPES.map((teamType) => {
                const teamMembers = groupedMembers[teamType.id];
                if (!teamMembers || teamMembers.length === 0) return null;

                return (
                  <motion.div
                    key={teamType.id}
                    initial={{ opacity: 0, y: 20 }}
                    whileInView={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.6 }}
                    viewport={{ once: true }}
                  >
                    <div className="text-center mb-8">
                      <h2 className="text-3xl font-bold text-secondary-900 mb-2">{teamType.name}</h2>
                      <p className="text-secondary-600">{teamType.description}</p>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                      {teamMembers.map((member, index) => (
                        <motion.div
                          key={member._id}
                          initial={{ opacity: 0, y: 20 }}
                          whileInView={{ opacity: 1, y: 0 }}
                          transition={{ duration: 0.6, delay: index * 0.1 }}
                          viewport={{ once: true }}
                          className="card text-center hover:shadow-lg transition-all duration-200"
                        >
                          <div className="w-24 h-24 mx-auto mb-4 rounded-full overflow-hidden bg-secondary-100">
                            {member.image?.url ? (
                              <img
                                src={member.image.url}
                                alt={member.name}
                                className="w-full h-full object-cover"
                              />
                            ) : (
                              <div className="w-full h-full flex items-center justify-center text-2xl font-bold text-secondary-600">
                                {member.name.charAt(0)}
                              </div>
                            )}
                          </div>

                          <h3 className="text-lg font-semibold text-secondary-900 mb-1">{member.name}</h3>
                          <p className="text-primary-600 font-medium mb-2">{member.role}</p>

                          {member.bio && (
                            <p className="text-sm text-secondary-600 mb-4 line-clamp-3">{member.bio}</p>
                          )}

                          {member.domains && member.domains.length > 0 && (
                            <div className="flex flex-wrap justify-center gap-1 mb-4">
                              {member.domains.map((domain) => (
                                <span
                                  key={domain}
                                  className="px-2 py-1 bg-primary-100 text-primary-800 text-xs rounded"
                                >
                                  {domain}
                                </span>
                              ))}
                            </div>
                          )}

                          {member.social && (
                            <div className="flex justify-center space-x-3">
                              {member.social.github && (
                                <a
                                  href={member.social.github}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-secondary-400 hover:text-secondary-600"
                                >
                                  <Github size={18} />
                                </a>
                              )}
                              {member.social.linkedin && (
                                <a
                                  href={member.social.linkedin}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-secondary-400 hover:text-secondary-600"
                                >
                                  <Linkedin size={18} />
                                </a>
                              )}
                              {member.social.portfolio && (
                                <a
                                  href={member.social.portfolio}
                                  target="_blank"
                                  rel="noopener noreferrer"
                                  className="text-secondary-400 hover:text-secondary-600"
                                >
                                  <Globe size={18} />
                                </a>
                              )}
                            </div>
                          )}
                        </motion.div>
                      ))}
                    </div>
                  </motion.div>
                );
              })}
            </div>
          )}

          {filteredMembers.length === 0 && !isLoading && (
            <div className="text-center py-12">
              <p className="text-secondary-600">No members found matching your criteria.</p>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default Members;
